PRAGMA defer_foreign_keys=TRUE;
ANALYZE sqlite_schema;
CREATE TABLE d1_migrations(
		id         INTEGER PRIMARY KEY AUTOINCREMENT,
		name       TEXT UNIQUE,
		applied_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
);
CREATE TABLE mcp_actions (
    action_id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL,
    mcp_server_name TEXT NOT NULL,
    tool_name TEXT NOT NULL,
    tokens_consumed INTEGER NOT NULL,
    success INTEGER DEFAULT 1,
    error_message TEXT,
    execution_time_ms INTEGER,
    parameters TEXT,
    created_at TEXT DEFAULT (datetime('now')),
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);
CREATE TABLE IF NOT EXISTS "transactions" (
  transaction_id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL REFERENCES users(user_id),
  type TEXT NOT NULL CHECK (type IN ('purchase', 'usage')),
  token_amount INTEGER NOT NULL,
  balance_after INTEGER NOT NULL,
  stripe_payment_id TEXT UNIQUE,  
  mcp_server_name TEXT,
  description TEXT,
  created_at TIMESTAMP DEFAULT (datetime('now'))
);
CREATE TABLE IF NOT EXISTS "users" (
  user_id TEXT PRIMARY KEY,
  email TEXT UNIQUE NOT NULL,
  current_token_balance INTEGER DEFAULT 0 CHECK (current_token_balance >= 0),
  monthly_token_limit INTEGER,
  total_tokens_purchased INTEGER DEFAULT 0,
  total_tokens_used INTEGER DEFAULT 0,
  stripe_customer_id TEXT,
  created_at TIMESTAMP DEFAULT (datetime('now')),
  last_login_at TIMESTAMP,
  
  is_deleted INTEGER DEFAULT 0 CHECK (is_deleted IN (0, 1)),
  deleted_at TIMESTAMP,
  workos_user_id TEXT  
);
CREATE TABLE account_deletions (
  deletion_id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  original_email TEXT NOT NULL,
  tokens_forfeited INTEGER NOT NULL,
  total_tokens_purchased INTEGER DEFAULT 0,
  total_tokens_used INTEGER DEFAULT 0,
  stripe_customer_id TEXT,
  deletion_reason TEXT,  
  deleted_at TIMESTAMP DEFAULT (datetime('now')),
  deleted_by_ip TEXT, stripe_customer_deleted INTEGER NOT NULL DEFAULT 0, stripe_deletion_error TEXT, mcp_actions_anonymized INTEGER NOT NULL DEFAULT 0, email_hash TEXT, failed_deductions_cleaned INTEGER NOT NULL DEFAULT 0, user_acknowledged_no_refund INTEGER NOT NULL DEFAULT 0, had_pending_checkout INTEGER NOT NULL DEFAULT 0, checkout_session_id TEXT, checkout_auto_refunded INTEGER NOT NULL DEFAULT 0, checkout_refund_id TEXT,    
  
  FOREIGN KEY (user_id) REFERENCES users(user_id)
);
CREATE TABLE failed_deductions (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    action_id TEXT NOT NULL UNIQUE,
    user_id TEXT NOT NULL,
    mcp_server_name TEXT NOT NULL,
    tool_name TEXT NOT NULL,
    token_amount INTEGER NOT NULL,
    parameters TEXT NOT NULL,
    error_message TEXT NOT NULL,
    created_at TEXT NOT NULL,
    resolved_at TEXT,
    retry_count INTEGER DEFAULT 0,
    last_retry_at TEXT, resolved INTEGER NOT NULL DEFAULT 0, resolution_note TEXT,
    FOREIGN KEY(user_id) REFERENCES users(user_id) ON DELETE CASCADE
);
CREATE TABLE api_keys (
  
  api_key_id TEXT PRIMARY KEY,

  
  user_id TEXT NOT NULL,

  
  api_key_hash TEXT NOT NULL UNIQUE,

  
  key_prefix TEXT NOT NULL,

  
  name TEXT NOT NULL,

  
  last_used_at INTEGER,

  
  created_at INTEGER NOT NULL,

  
  expires_at INTEGER,

  
  is_active INTEGER DEFAULT 1 CHECK (is_active IN (0, 1)),

  
  FOREIGN KEY (user_id) REFERENCES users(user_id) ON DELETE CASCADE
);
DELETE FROM sqlite_sequence;
CREATE INDEX idx_mcp_actions_user_id ON mcp_actions(user_id);
CREATE INDEX idx_mcp_actions_server_name ON mcp_actions(mcp_server_name);
CREATE INDEX idx_mcp_actions_created_at ON mcp_actions(created_at DESC);
CREATE INDEX idx_transactions_user_id ON transactions(user_id);
CREATE INDEX idx_transactions_stripe_payment ON transactions(stripe_payment_id);
CREATE INDEX idx_transactions_created_at ON transactions(created_at DESC);
CREATE INDEX idx_transactions_type ON transactions(type);
CREATE INDEX idx_users_email ON users(email);
CREATE INDEX idx_users_stripe_customer ON users(stripe_customer_id);
CREATE INDEX idx_users_is_deleted ON users(is_deleted);
CREATE INDEX idx_users_deleted_at ON users(deleted_at);
CREATE INDEX idx_account_deletions_user_id ON account_deletions(user_id);
CREATE INDEX idx_account_deletions_deleted_at ON account_deletions(deleted_at DESC);
CREATE INDEX idx_account_deletions_original_email ON account_deletions(original_email);
CREATE UNIQUE INDEX idx_mcp_actions_action_id
ON mcp_actions(action_id);
CREATE INDEX idx_failed_deductions_unresolved
ON failed_deductions(resolved_at, created_at)
WHERE resolved_at IS NULL;
CREATE INDEX idx_account_deletions_stripe_failed
ON account_deletions(stripe_customer_deleted)
WHERE stripe_customer_deleted = 0 AND stripe_customer_id IS NOT NULL;
CREATE INDEX idx_account_deletions_email_hash
ON account_deletions(email_hash);
CREATE INDEX idx_account_deletions_recent_checkout
ON account_deletions(deleted_at, had_pending_checkout)
WHERE had_pending_checkout = 1;
CREATE INDEX idx_api_keys_user_id ON api_keys(user_id);
CREATE INDEX idx_api_keys_hash ON api_keys(api_key_hash);
CREATE INDEX idx_api_keys_active ON api_keys(is_active);
CREATE INDEX idx_api_keys_user_active ON api_keys(user_id, is_active);
